import React, { Component } from 'react';
import '../styles/todo.css';

export default class Todo extends Component {
    constructor(props) {
        super(props);
        this.state = {
            list: [],
        };
        this.addTodo = this.addTodo.bind(this);
        this.removeTodo = this.removeTodo.bind(this);
    }

    addTodo(e) {
        e.preventDefault();
        let newList = this.state.list;

        const newTodo = document.getElementById('addTodo').value;
        const form = document.getElementById('form').value;

        if (newTodo != '') {
            newList.push(newTodo);
            this.setState({
                list: newList,
            });
        }
        newTodo.classList.remove('is-danger');
        form.reset();
    }
    removeTodo(item) {
        let list = this.state.list.slice();
        list.map((el, i) => {
            if (el === item) {
                list.splice(i, 1);
                console.log('match');
            }
        });

        this.setState({
            list: list,
        });
    }
    render() {
        console.log('kör');

        return (
            <div>
                <section className="todoContainer">
                    <h1>Todo List</h1>
                    <form className="todoElements" id="form">
                        <input type="text" placeholder="Add todo" id="addTodo"></input>
                        <button className="todoButton" onClick={this.addTodo}>
                            Add
                        </button>
                    </form>
                    <ul>
                        {this.state.list.map((item, i) => (
                            <li className="todoItem" key={i}>
                                {item}
                                <div className="removeButton" onClick={() => this.removeTodo(item)}>
                                    X
                                </div>
                            </li>
                        ))}
                    </ul>
                </section>
            </div>
        );
    }
}
