import React from 'react';
import '../styles/navbar.css';

function ItemNav({selected, onUpdateItem}){
    const tabs = ['Home', 'About']

    return (
            <ul className='navbar'>{tabs.map((tab) => (
                <li key={tab}>
                    <button style={tab === selected ? {color:'grey'}:null}
                     onClick={()=>onUpdateItem(tab)}>{tab}</button>
                    </li>
            ))}</ul>

    )
}

export default class Navbar extends React.Component {
    constructor(props) {
        super(props)

        this.state = {
          selectedTab: null
        }

        this.updateTab = this.updateTab.bind(this)
      }

      updateTab(selectedTab){
          this.setState({selectedTab})
      }


    render(){  
        const {selectedTab} = this.state;
        <ItemNav selected={selectedTab} onUpdateItem={this.updateTab}  />

}
}
